CREATE DATABASE appetitedb;

\c appetitedb;

CREATE TABLE categorias (
  id SERIAL PRIMARY KEY,
  nome VARCHAR(100) NOT NULL
);

CREATE TABLE receitas (
  id SERIAL PRIMARY KEY,
  titulo VARCHAR(255) NOT NULL,
  descricao TEXT,
  ingredientes TEXT,
  modo_preparo TEXT,
  imagem TEXT,
  favorita BOOLEAN DEFAULT FALSE,
  avaliacao INTEGER CHECK (avaliacao IS NULL OR (avaliacao >= 1 AND avaliacao <= 5)),
  tempo_preparo INTEGER,
  dificuldade VARCHAR(30) CHECK (dificuldade IN ('FACIL', 'MEDIO', 'DIFICIL')) NOT NULL,
  data_criacao TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  categoria_id INTEGER REFERENCES categorias(id) ON DELETE SET NULL,
  UNIQUE (titulo)
);

CREATE TABLE receita_categorias (
  receita_id INTEGER REFERENCES receitas(id) ON DELETE CASCADE,
  categoria_id INTEGER REFERENCES categorias(id) ON DELETE CASCADE,
  PRIMARY KEY (receita_id, categoria_id)
);

CREATE TABLE favoritos (
  id SERIAL PRIMARY KEY,
  receita_id INTEGER NOT NULL REFERENCES receitas(id) ON DELETE CASCADE,
  usuario_id INTEGER NOT NULL,
  data_criacao TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  UNIQUE(receita_id, usuario_id)
);

CREATE INDEX idx_favoritos_usuario ON favoritos(usuario_id);
CREATE INDEX idx_favoritos_receita ON favoritos(receita_id);



